//AppointmentForm.js
import React, { useMemo } from "react";
import { ContactPicker } from "../contactPicker/ContactPicker";

const getTodayString = () => {
  const [month, day, year] = new Date()
    .toLocaleDateString("en-US")
    .split("/");
  return `${year}-${month.padStart(2, "0")}-${day.padStart(2, "0")}`;
};

export const AppointmentForm = ({
  contacts,
  name,
  setTitle,
  contact,
  setContact,
  date,
  setDate,
  time,
  setTime,
  handleSubmit
}) => {
  console.log('Rendering AppointmentForm'); // Add console.log here
  const contactNames = useMemo(() => {
    console.log('Calculating contactNames'); // Add console.log here
    return contacts.map((contact) => contact.name);
  }, [contacts]);

  const handleAddAppointment = (e) => {
    console.log('Add Appointment button clicked'); // Add console.log here

    // Call the original handleSubmit function
    handleSubmit(e);
  };

  return (
    <form onSubmit={handleAddAppointment}> {/* Use handleAddAppointment */}
      <label>
        Appointment Name:
        <input
          type="text"
          name="name"
          value={name}
          onChange={(e) => setTitle(e.target.value)}
          required
          placeholder="Appointment Name"
        />
      </label>
      <br />
      <label>
        Contact:
        <ContactPicker
          name="contact"
          value={contact}
          contacts={contactNames}
          onChange={(e) => setContact(e.target.value)}
        />
      </label>
      <br />
      <label>
        Date:
        <input
          type="date"
          name="date"
          min={getTodayString()}
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
        />
      </label>
      <br />
      <label>
        Time:
        <input
          type="time"
          name="time"
          value={time}
          onChange={(e) => setTime(e.target.value)}
          required
        />
      </label>
      <br />
      <input type="submit" value="Add Appointment" />
    </form>
  );
};
